import React, { useState } from "react";
import './index.css';
import FinalYearProjectLogo from './Images/FinalYearProjectLogo.jpg'
import LiveLogo from './Images/Live_Logo.jpg'
const App = () => {
  const [confBtnTitle, setConfBtnTitle] = useState('Send Message');
  const [confBtnTitle1, setConfBtnTitle1] = useState('Send Message');
  const [confBtnTitle2, setConfBtnTitle2] = useState('Send Message');
  const [confBtnStyle, setConfBtnStyle] = useState({})
  const [confBtnStyle1, setConfBtnStyle1] = useState({})
  const [confBtnStyle2, setConfBtnStyle2] = useState({})
  const [index, setIndex] = useState();
  const sendMessage = (e) => {
    console.log(Number(e.target.id))
    setConfBtnTitle((prevState) => {
      if (prevState != 'Send Message') {
        return 'Send Message';
      } else {
        return 'Cancel Message '
      }
    })
    setConfBtnStyle((prevState) => {

      if (Object.keys(prevState).length === 0 && prevState.constructor === Object) {
        return ({
          backgroundColor: 'red'
        })
      } else {
        return ({})
      }
    })

  }
  const sendMessage1 = () => {
    setConfBtnTitle1((prevState) => {
      if (prevState != 'Send Message') {
        return 'Send Message';
      } else {
        return 'Cancel Message '
      }
    })
    setConfBtnStyle1((prevState) => {

      if (Object.keys(prevState).length === 0 && prevState.constructor === Object) {
        return ({
          backgroundColor: 'red'
        })
      } else {
        return ({})
      }
    })

  }
  const sendMessage2 = () => {
    setConfBtnTitle2((prevState) => {
      if (prevState != 'Send Message') {
        return 'Send Message';
      } else {
        return 'Cancel Message '
      }
    })
    setConfBtnStyle2((prevState) => {

      if (Object.keys(prevState).length === 0 && prevState.constructor === Object) {
        return ({
          backgroundColor: 'red'
        })
      } else {
        return ({})
      }
    })

  }



  return (
    <div className="main_container">
      <div className="header">
        <img src={FinalYearProjectLogo} alt="Logo" className="logo" />
        <h1 className="heading">Accident Tracking and Reporting system for vehicles</h1>
        {/* <div className="main_live" >
          <span className="red_live"></span><span className="live">Live</span>
        </div> */}
        <img src={LiveLogo} alt="LiveLogo" className="live_logo" />
      </div>
      <div className='table_data'>
        <table className=" table table-hover ">
          <thead className=' fs-3 table_head_data'>
            <tr>
              <th scope="col">S NO.</th>
              <th scope="col">Mobile Number</th>
              <th scope="col">Vehicle Reg. Number</th>
              <th scope="col">Accident Location</th>
              <th scope="col">Confirmation Message</th>
              <th scope="col">Message Response</th>
              <th scope="col">Ambulance Status</th>

            </tr>
          </thead>
          <tbody className='fs-5 text-black'>
            {/* {data.map((val, idx) => {
                                return <tr key={idx}>
                                    <th scope="row">{val.state}</th>
                                    <td>{val.active}</td>
                                    <td>{val.confirmed}</td>
                                    <td>{val.deaths}</td>
                                    <td>{val.lastupdatedtime}</td>
                                </tr>
                            })} */}
            <tr >
              <th scope="row">1</th>
              <td>9897534395</td>
              <td>12586142bdf565</td>
              <td>XXXXXXXXXXXX</td>
              <td><button type="button" id={index} style={confBtnStyle} onClick={sendMessage} className="fs-5 btn-1 btn btn-success">{confBtnTitle}</button></td>
              <td>---</td>
              <td><button type="button" className="fs-5 btn-1 btn btn-success">Depart</button></td>
            </tr>
            <tr >
              <th scope="row">2</th>
              <td>7897534395</td>
              <td>52586142bdf565</td>
              <td>XXXXJEFEKNFX</td>
              <td><button type="button" style={confBtnStyle1} onClick={sendMessage1} className="fs-5 btn-1 btn btn-success">{confBtnTitle1}</button></td>
              <td>---</td>
              <td><button type="button" className="fs-5 btn-1 btn btn-success">Depart</button></td>
            </tr>
            <tr >
              <th scope="row">3</th>
              <td>6397534395</td>
              <td>25886142bdf565</td>
              <td>XXJENFKJXXXXXXX</td>
              <td><button type="button" style={confBtnStyle2} onClick={sendMessage2} className="fs-5 btn-1 btn btn-success">{confBtnTitle2}</button></td>
              <td>---</td>
              <td><button type="button" className="fs-5 btn-1 btn btn-success">Depart</button></td>
            </tr>

          </tbody>
        </table>
      </div>

    </div>

  );
}

export default App;
